mapper.insert:
insert into T_STOCK_TRAINNING_INFO (
stock_id
,s_date
,open_price
,close_price
,max_price
,min_price
)
values
(
    #{item.stock_id}
    ,#{item.s_date}
    ,#{item.open_price}
    ,#{item.close_price}
    ,#{item.max_price}
    ,#{item.min_price}
    )
ON CONFLICT(stock_id) DO NOTHING

